import React, { useRef, useState } from "react";
import Webcam from "react-webcam";
import "./App.css";

function App() {
  const webcamRef = useRef(null);
  const [screenshot, setScreenshot] = useState(null);

  const videoConstraints = {
    width: 640,
    height: 480,
    facingMode: "user",
  };

  const captureImage = () => {
    const imageSrc = webcamRef.current.getScreenshot();
    setScreenshot(imageSrc);
    console.log("Screenshot captured");
    // In Phase 2: Send imageSrc to backend via POST request
  };

  return (
    <div className="App">
      <header className="App-header">
        <h2>üì∏ Online Exam Proctor</h2>

        <Webcam
          audio={false}
          ref={webcamRef}
          screenshotFormat="image/jpeg"
          width={640}
          height={480}
          videoConstraints={videoConstraints}
        />

        <button onClick={captureImage} style={{ marginTop: "15px" }}>
          üì∑ Capture Image
        </button>

        {screenshot && (
          <div style={{ marginTop: "20px" }}>
            <h4>üñºÔ∏è Captured Image:</h4>
            <img src={screenshot} alt="Captured" style={{ width: "320px", borderRadius: "10px" }} />
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
